== Sample code ==

Read the [http://booch95.sourceforge.net/case-study.html project tutorial] for full detail.

 {{Ada/kw|with}} {{Ada/pk|Ada|Calendar}};
 {{Ada/kw|with}} {{Ada/pk|Ada|Strings|Bounded}};

 {{Ada/kw|package}} Cars {{Ada/kw|is}}

    {{Ada/kw|package}} Plate_Strings
      {{Ada/kw|is}} {{Ada/kw|new}} Ada.Strings.Bounded.Generic_Bounded_Length (10);

    {{Ada/kw|subtype}} Plate_String {{Ada/kw|is}} Plate_Strings.Bounded_String;

    {{Ada/kw|package}} Model_Strings
      {{Ada/kw|is}} {{Ada/kw|new}} Ada.Strings.Bounded.Generic_Bounded_Length (32);

    {{Ada/kw|subtype}} Model_String {{Ada/kw|is}} Model_Strings.Bounded_String;

    {{Ada/kw|type}} Car {{Ada/kw|is}} 
       {{Ada/kw|record}}
          Plate : Plate_String;
          Model : Model_String;
          Registered : Ada.Calendar.Time;
       {{Ada/kw|end}} {{Ada/kw|record}};

 {{Ada/kw|end}} Cars;

 {{Ada/kw|with}} BC.Containers.Collections.Bounded;
 {{Ada/kw|with}} Cars;
 {{Ada/kw|package}} My_Fleet_Combined {{Ada/kw|is}}

    {{Ada/kw|use}} {{Ada/kw|type}} Cars.Car;

    {{Ada/kw|package}} Abstract_Car_Containers 
      {{Ada/kw|is}} {{Ada/kw|new}} BC.Containers (Cars.Car);

    {{Ada/kw|package}} Abstract_Car_Collections 
      {{Ada/kw|is}} {{Ada/kw|new}} Abstract_Car_Containers.Collections;

    {{Ada/kw|package}} Fleets 
      {{Ada/kw|is}} {{Ada/kw|new}} Abstract_Car_Collections.Bounded (Maximum_Size => 30);

    The_Fleet : Fleets.Collection;

 {{Ada/kw|end}} My_Fleet_Combined;

