 // create chat_doorbell sound
 chat_doorbell = new Sound(this);
 chat_doorbell.attachSound("doorbell.wav");
 chat_doorbell.setVolume(RING_VOLUME);
 chat_doorbell.stop();
 trace("chat_doorbell: " + chat_doorbell);
