== Not sure what this is, but it was on intro page ==

This was on the intro page. Someone might have been trying to do something with it at one time, but did not clarify what.

clc;
clear all;
close all;

[filename, pathname] = uigetfile( {'*.jpg';'*.bmp'});
aa=imread([pathname,filename]);
I=aa;
bb=imresize(aa,[256 256]);
imshow(bb);title('input image');

%%...............%%% RGB2Lab color conversion %%%.........

rgbimage=makecform('srgb2lab');
labimage=applycform(bb,rgbimage);
figure,
imshow(labimage);title('color conversion image');

%%..............%%% seperate L* a* b* color space %%%.........

L=im2double(labimage(:,:,1));
a=im2double(labimage(:,:,2));                                                                                                         
b=im2double(labimage(:,:,3));
figure,imshow(L);title('L* image');
[h,w,o]=size(L);

%%..........%%% applying filter in L* color space %%%..........

for s=1:3
    W(s)=h/2^s;
    for i=1:h
        for j=1:w
           p=(i-W(s));
           q=(i+W(s));
           r=(j-W(s));
           t=(j+W(s));
           if p<=0
              p=1;
           end
           if q>256
              q=256;
           end
           if r<=0
              r=1;
           end
           if t>256
              t=256;
           end
           m=p:q;
           n=r:t;
           %N(s)=W(s)*W(s);
           N(s)=size(m,2)*size(n,2);
           sum1=sum(sum(L(m,n)));
           V=1/N(s);
           A=V*sum1;

           B(i,j)=L(i,j)-A;                                                      
        end
    end

    %B=L-A;
    %figure,imshow(B);
    azx(:,:,s)=B;

end

contrast_L=azx(:,:,1)+azx(:,:,2)+azx(:,:,3);
figure,imshow(contrast_L),title('contrast map L* image');

%%..............%%% find inverse of a* %%%............

figure,imshow(a);title('a* image');
d=max(max(a));
yy=d-a;
yy=(yy*5);
figure,imshow(yy);title('inv a* image');
[h,w,o]=size(yy);

%%.............%%% applying filter in a* color space %%%........

for s=1:3
    W(s)=h/2^s;
    for i=1:h
        for j=1:w
           p=(i-W(s));
           q=(i+W(s));
           r=(j-W(s));
           t=(j+W(s));
           if p<=0
              p=1;
           end
           if q>256
              q=256;
           end
           if r<=0
              r=1;
           end
           if t>256
              t=256;
           end
           m=p:q;
           n=r:t;
           %N(s)=W(s)*W(s);
           N(s)=size(m,2)*size(n,2);
           sum1=sum(sum(yy(m,n)));
           V=1/N(s);
           A=V*sum1;

           C(i,j)=yy(i,j)-A;                                                      
        end
    end

    %C=L-A;
    %figure,imshow(C);
    aza(:,:,s)=C;

end

contrast_a=aza(:,:,1)+aza(:,:,2)+aza(:,:,3);
figure,imshow(contrast_a),title('contrast map a* image');

%%.............%%% find final scaling contrast map %%%.......

Scaling_contrast=imadd(contrast_L,contrast_a);
figure,imshow(Scaling_contrast),title('Scaling contrast map');

%%...........%%% gabor filter outputs %%%.......................

[gabor_timage,u,v,GT,filter]=final_gabor(aa);

%%..........%%% removing erythema & dark pixels %%%..........

ts=0.4;                                 %% set threshold value
R=Scaling_contrast;
for i=1:h
    for j=1:w
        if Scaling_contrast(i,j)>=ts
            R(i,j)=1;
        else
            R(i,j)=0;
        end
    end
end

figure,imshow(R);title('Removing erythema');

