=='O cuórpo - the body== {| class="wikitable" |style="width:100px", bgcolor="#e9e3c5"|'''Neapolitan''' |style="width:100px", bgcolor="#e9e3c5"| '''English''' |- |align="left", bgcolor="#c9c8e9"|'a capa |align="left"|head |- |align="left", bgcolor="#c9c8e9"|'e capille |align="left"|hair |- |align="left", bgcolor="#c9c8e9"|'o vraccio |align="left"|arm |- |align="left", bgcolor="#c9c8e9"|'a gamma |align="left"|leg |- |align="left", bgcolor="#c9c8e9"|'e rine |align="left"|back |- |align="left", bgcolor="#c9c8e9"|'o stòmmaco |align="left"|stomach |- |align="left", bgcolor="#c9c8e9"|'a mana |align="left"|hand |- |align="left", bgcolor="#c9c8e9"|'a vocca |align="left"|mouth |- |align="left", bgcolor="#c9c8e9"|'o dènte |align="left"|tooth |- |align="left", bgcolor="#c9c8e9"|ll'uócchio |align="left"|eye |- |align="left", bgcolor="#c9c8e9"|'a recchia |align="left"|ear |- |align="left", bgcolor="#c9c8e9"|'o dito |align="left"|finger |- |align="left", bgcolor="#c9c8e9"|'o pède |align="left"|foot |- |align="left", bgcolor="#c9c8e9"|'o naso |align="left"|nose |- |align="left", bgcolor="#c9c8e9"|'o denùcchio |align="left"|knee |- |align="left", bgcolor="#c9c8e9"|'o cuóllo |align="left"|neck |- |align="left", bgcolor="#c9c8e9"|'o gùveto |align="left"|elbow |- |align="left", bgcolor="#c9c8e9"|'o mascariéllo |align="left"|cheek |- |align="left", bgcolor="#c9c8e9"|'a spàlla |align="left"|shoulder |- |align="left", bgcolor="#c9c8e9"|'o vellìculo |align="left"|belly button |- |align="left", bgcolor="#c9c8e9"|'a nasècchia |align="left"|nostril |- |align="left", bgcolor="#c9c8e9"|'o piétto |align="left"|chest |- |align="left", bgcolor="#c9c8e9"|'o pésce |align="left"|penis (''volg.'') |- |align="left", bgcolor="#c9c8e9"|'a pucchiàcca |align="left"|vagina (''volg.'') |- |align="left", bgcolor="#c9c8e9"|'a guàllera |align="left"|balls (''volg.'') |- |align="left", bgcolor="#c9c8e9"|'a coscia |align="left"|thigh |}
